mapper.sql:
SELECT
bpm.f_id id, bpm.f_enabled_mark enabledMark, bpm.f_platform platform, bpm.f_description description, bpm.F_Tenant_Id tenantId, bpm.f_sort_code sortCode, bpm.f_system_id systemId, bpm.f_portal_id portalId, bpm.f_creator_time creatorTime, bpm.f_last_modify_time lastModifyTime,
bp.f_category categoryId, bp.f_full_name portalName, bd.f_full_name categoryName,
us.F_Real_Name createUserName, us.F_Account createUserAccount, us2.F_Real_Name modifyUserName, us2.F_Account modifyUserAccount FROM base_portal_manage bpm LEFT JOIN base_portal bp ON bpm.f_portal_id = bp.f_id LEFT JOIN base_user us ON bpm.F_Creator_User_Id = us.f_id LEFT JOIN base_user us2 ON bpm.F_Last_Modify_User_Id = us2.f_id LEFT JOIN base_dictionary_data bd ON bp.f_category = bd.f_id WHERE 1 = 1
AND (bp.f_full_name LIKE #{pmPage.keyword} OR bpm.f_description LIKE #{pmPage.keyword})
AND bp.f_category = #{pmPage.category}
AND bpm.f_enabled_mark = #{pmPage.enabledMark}
AND bpm.f_platform LIKE #{pmPage.platform}
AND bpm.f_system_id = #{pmPage.systemId}
AND bp.F_state != #{pmPage.state}

mapper.select:
SELECT bpm.f_id id, bpm.f_enabled_mark enabledMark, bpm.f_platform platform, bpm.f_description description, bpm.f_sort_code sortCode, bpm.f_system_id systemId, bpm.f_portal_id portalId, bpm.f_creator_time creatorTime, bpm.f_last_modify_time lastModifyTime, bp.f_category categoryId, bp.f_full_name portalName, bd.f_full_name categoryName FROM base_portal bp LEFT JOIN base_portal_manage bpm ON bpm.f_portal_id = bp.f_id LEFT JOIN base_dictionary_data bd ON bp.f_category = bd.f_id WHERE bpm.f_system_id IN
#{systemId}
AND bpm.f_id IN
#{managerId}
AND bpm.f_enabled_mark = 1 ORDER BY bpm.f_sort_code ASC, bpm.f_creator_time DESC
